import React, { Component } from "react"

class Book extends Component{
  handleChange(e){
    let bookUpdated = book
    bookUpdated.shelfName = e.target.value
    this.props.updateBook(bookUpdated)
  }
  render(){
    const book = this.props.book;
    return(
      <div className="book">
        <div className="book-top">
          <div className="book-cover" style={{ width: 128, height: 193, backgroundImage: `url(${book.coverURL})` }}></div>
          <div className="book-shelf-changer">
            <select onChange={this.handleChange}>
              <option value="move" disabled>Move to...</option>
              <option value="currentlyReading">Currently Reading</option>
              <option value="wantToRead">Want to Read</option>
              <option value="read">Read</option>
              <option value="none">None</option>
            </select>
          </div>
        </div>
        <div className="book-title">{book.name}</div>
        <div className="book-authors">{book.name}</div>
      </div>
    )
  }
}

export default Book
