import React, { Component } from "react"

class Book extends Component{
  state={
    book: this.props.book
  }

  componentDidMount(){
    this.setState({book: this.props.book})
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(e){
    let book = this.state.book
    book.shelfName = e.target.value
    this.setState({
      book: book
    })
    this.props.updateBook(this.state.book)
  }
  render(){
    return(
      <div className="book">
        <div className="book-top">
          <div className="book-cover" style={{ width: 128, height: 193, backgroundImage: `url(${this.state.book.coverURL})` }}></div>
          <div className="book-shelf-changer">
            <select value= {this.state.book.shelfDisplayName} onChange={this.handleChange}>
              <option value="move" disabled>Move to...</option>
              <option value="currentlyReading">Currently Reading</option>
              <option value="wantToRead">Want to Read</option>
              <option value="read">Read</option>
              <option value="none">None</option>
            </select>
          </div>
        </div>
        <div className="book-title">{this.state.book.name}</div>
        <div className="book-authors">{this.state.book.name}</div>
      </div>
    )
  }
}

export default Book
